import './Logo.png';
import Certifications from './Certifications';
import gifImage from './dance.gif';
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap";
import Rate from './Rate.js';
import About from './About.js';
import * as Three from 'three';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import Music from './Music.js';
import TodoList from './TodoList';

function App() {
  const [theme, setTheme] = useState('light');
  const [currentTime, setCurrentTime] = useState(new Date());
  const [isNavAnimated, setIsNavAnimated] = useState(false);
  const [isChangeImage, setChangeImage] = useState(false);
  const [git, setGit] = useState(true);

  function handleNavigation() {
    setIsNavAnimated(true);
    setTimeout(() => {
      setIsNavAnimated(false);
    }, 500);
  }

  function changeImage() {
    setChangeImage((prevState) => !prevState);
  }

  function toggleTheme() {
    setTheme((prevTheme) => (prevTheme === 'light' ? 'dark' : 'light'));
  }

  function Home() {
    return (
      <div className={`${isNavAnimated ? 'fade-in' : ''}`}>
        <div className="image-container">
          <img src={require('./port.jpg')} className="profile-image" width="200" />
        </div>

        <p className="first-paragraph">"Aspiring web developer"</p>
      </div>
    );
  }

  function Projects() {
    return (
      <div style={{ gap: 100, display: 'flex', flexDirection: 'row' }} className={`${isNavAnimated ? 'fade-in' : ''}`}>
        {/* Your project cards and content here */}
      </div>
    );
  }

  function About() {
    return (
      <div className={`${isNavAnimated ? 'fade-in' : ''}`}>
        {/* Your educational background timeline content here */}
      </div>
    );
  }

  function Contact() {
    // Contact component code here
  }

  return (
    <div className={`App Full-page ${theme}`}>
      <div className="uppperbody">
        <Router>
          <nav>
            <div className="container">
              <ul className="horizontal-list row">
                {/* Navigation links here */}
              </ul>
            </div>
          </nav>

          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/projects" element={<Projects />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="/music" element={<Music />} />
            <Route path="/certifications" element={<Certifications additionalInfo={isNavAnimated} />} />
          </Routes>
        </Router>
      </div>

      <div className="Footer">
        <ul className="horizontal-list">
          {/* Social media icons and links here */}
        </ul>
      </div>
    </div>
  );
}

export default App;
